public class ejercicio3 {
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int X;
		int Y;
		double N;
		double M;
		//valores
		X=10;
		Y=20;
		N=30;
		M=40;
		
		
		System.out.println("X" + X);
		System.out.println("Y" + Y);
		System.out.println("N" + N);
		System.out.println("M" + M);
		
		//calculos de X,Y
		int suma=X+Y;
		System.out.println(suma);
		int resta=X-Y;
		System.out.println(resta);
		int multiplicacion=X*Y;
		System.out.println(multiplicacion);
		int division=X/Y;
		System.out.println(division);
		int modulo=X%Y;
		System.out.println(modulo);
		
		//calculos de N,M
		double suma2=N+M;
		System.out.println(suma2);
		double resta2=N-M;
		System.out.println(resta2);
		double multiplicacion2=N*M;
		System.out.println(multiplicacion2);
		double division2=N/M;
		System.out.println(division2);
		double modulo2=N%M;
		System.out.println(modulo2);
		
		//calculos de Y,M
		double suma3=Y+M;
		System.out.println(suma3);
		double division3=Y/M;
		System.out.println(division3);
		double modulo3=Y%M;
		System.out.println(modulo3);
	}
}
